import React , {useState} from "react"
import Calendar from 'react-calendar'
import 'react-calendar/dist/Calendar.css';
import './App.css';

function App() {
  const [but,setBut] = useState(false);
  const [date,setDate] = useState(new Date());
  const [focus,setFocus]=useState(false);
  const calChange = (e) =>{
     setDate(e); 
  }

  return (
    <div className="App">
       <button onClick={()=>setBut(but===false?true:false)}>Show Calendar</button>
       {but?<Calendar onChange={calChange} value={date}/>:<p>I am that close</p>}
       <div style={{width:"60%",display:"flex",flexDirection:"column",backgroundColor:"orange",justifyContent:"center",alignItems:"center"}}>
         <input style={{width:"100%"}}type="text" placeholder="title" onFocus={()=>setFocus(true)}/>
         {focus?<div>
          <div style={{width:"100%"}}><input style={{width:"100%"}} type="text" placeholder="Enter Text"/></div><div><br/></div>
         </div>:null}
       </div>


    </div>
  );
}

export default App;
