import React , {useState} from "react"
import Calendar from 'react-calendar'
import 'react-calendar/dist/Calendar.css';
import './App.css';

function App() {
  const [but,setBut] = useState(false);
  const [date,setDate] = useState(new Date());
  const [focus,setFocus]=useState(false);
  const calChange = (e) =>{
     setDate(e); 
  }

  return (
    <div className="App">
       <button onClick={()=>setBut(but===false?true:false)}>Show Calendar</button>
       {but?<Calendar onChange={calChange} value={date}/>:<p>I am that close</p>}
       <div style={{display:"flex",flexDirection:"column",backgroundColor:"gray"}}>
         <input type="text" placeholder="title" onFocus={()=>setFocus(true)}/>
         <textarea rows="5" cols="40" placeholder="Enter your note "/>
       </div>


    </div>
  );
}

export default App;
