import React from 'react'
import searchIcon from "./assets/images/search.png"
import { MdKeyboardArrowDown,MdKeyboardCapslock } from "react-icons/md";
import DisplayNoteUtility from './DisplayNoteUtility';
import "../App.css"
export let searchData = [];
const SearchNote=({searchActive,setSearchActive,defaultNote,dateObj,activateNote,setActivateNote,dateActive,setDateActive,dateState,setDateState,date,setDate,month,setMonth,year,setYear,title,setTitle,note,setNote,noteAdded,setNoteAdded})=> {
    
    const searchHandler =(e) =>{
        
        let search = e.target.value;
         
        if(search.length>=1)
            { setSearchActive(true)
              searchData = noteAdded.filter(noteObj=> noteObj.Title.includes(search))
            }
        console.log(searchData); 
    }    
    
    return (
        <>
           <div className="searchComponent">
           <h1 className="Heading">MindiFy</h1>
           <div className="searchBar">
               <div className="search">
                  <img src={searchIcon} alt="search-icon" />
                  <input type="search" className="searchText" placeholder="Search Your Note" onChange={searchHandler} />
               </div>
               <div className="vertical"></div>
               <div className="filter">
                   <button className="filterButton">Filter <MdKeyboardArrowDown style={{verticalAlign: "middle",fontSize: "24px",}}/></button>
                   <div className="filterOptions">
                       <p>New</p>
                       <p>Year</p>
                       <p>Month</p>
                       <p>Day</p>
                   </div>
               </div>
           </div>
        </div>

        <br className="breakLine"/>
        <DisplayNoteUtility  activateNote={activateNote} setActivateNote={setActivateNote}  setTitle={setTitle} setNote={setNote} noteAdded={searchData} setNoteAdded={setNoteAdded}/>
        </>

    )
}

export default SearchNote
