import React , {useState} from 'react'
import AddNoteUtility from './AddNoteUtility';
import moment from "moment"
import "../App.css"
const AddNewNote = () => {
    const dateObj=new Date();
    const [activateNote,setActivateNote] = useState(false);
    const [dateActive,setDateActive] = useState(false);
    const [dateState,setDateState] = useState(dateObj);
    const [date,setDate] = useState(dateObj.getDate());
    const [month,setMonth] = useState(dateObj.getMonth()+1);
    const [year,setYear] = useState(dateObj.getFullYear());
    console.log(`Date : ${date} \n Month : ${moment(month).format("MM")} \n Year : ${year}`)
    return (
            <div>
            <div className="addNoteComponent">
            <h1 className="addNoteHeading">
                Add New Note
            </h1>
            <div className="noteBox">
               <div className="noteBoxContainer">
                  <input type="text" className="addTitle" placeholder="Title" onFocus={()=>setActivateNote(true)}/>
                  {
                      activateNote?<><textarea className="addNote" placeholder="Take a Note" rows="5" cols="10" />  <AddNoteUtility dateActive={dateActive} setDateActive={setDateActive} dateState={dateState} setDateState={setDateState} setDate={setDate} setMonth={setMonth} setYear={setYear} /></>:null 
                  }
               </div>
            </div>
           
        </div>
            </div>
    )
}

export default AddNewNote
