import React , {useState} from 'react'
import AddNoteUtility from './AddNoteUtility';
import DisplayNoteUtility from './DisplayNoteUtility';
import nextId from "react-id-generator";

import "../App.css"
export let noteDatabase =[]
const AddNewNote = ({defaultNote,dateObj,activateNote,setActivateNote,dateActive,setDateActive,dateState,setDateState,date,setDate,month,setMonth,year,setYear,title,setTitle,note,setNote,noteAdded,setNoteAdded}) => {

    noteDatabase=[
        {
            noteAdded:noteAdded,
            setNoteAdded:setNoteAdded,
            note:note,
            title:title,
        }
    ]
    return (
        <div>
            <div className="addNoteComponent">
            <h1 className="addNoteHeading">
                Add New Note
            </h1>
            <div className="noteBox">
               <div className="noteBoxContainer">
                  <input type="text" className="addTitle"  placeholder="Title" value={title} onFocus={()=>setActivateNote(true)} onChange={(e)=>{setTitle(e.target.value)}}/>
                  {
                      activateNote?<><textarea className="addNote" placeholder="Take a Note" value={note}  rows="5" cols="10" onChange={(e)=>setNote(e.target.value)} />  <AddNoteUtility dateActive={dateActive} setDateActive={setDateActive} dateState={dateState} setDateState={setDateState} setDate={setDate} setMonth={setMonth} setYear={setYear} title={title} note={note} setTitle={setTitle} setNote={setNote} setNoteAdded={setNoteAdded} date={date} month={month} year={year} fullDate={dateState}/></>:null 
                  }
               </div>
            </div>
           <div className="displayNote">
               <h1 className="displayHeading">Your Note's</h1>
               {
                  noteAdded.map(({Id,Title,Note,FullDate},index)=>{
                      return(
                        <div className="noteBox" key={Id}>
                           <div className="noteBoxContainer">
                              <input type="text" className="addTitle"  placeholder="Title" value={Title}  onInput={(e)=>{setTitle(e.target.value)}}/>
                              {
                               <><textarea className="addNote" placeholder="Take a Note"  value={Note} rows="5" cols="10" onChange={(e)=>setNote(e.target.value)} /> <DisplayNoteUtility setActivateNote={setActivateNote} Id={Id} title={Title} note={Note} setTitle={setTitle} setNote={setNote} setNoteAdded={setNoteAdded} noteAdded={noteAdded} activateNote={activateNote} /> </>
                              }
                           </div>
                           <h1>{FullDate}</h1>
                       </div>
                      )
                  })
               }
           </div>
        </div>
            </div>
    )
}

export default AddNewNote
