import React , {useState} from 'react'
import AddNoteUtility from './AddNoteUtility';
import "../App.css"
const AddNewNote = () => {
    const date=new Date();
    const [activateNote,setActivateNote] = useState(false);
    const [dateActive,setDateActive] = useState(false);
    const [dateState,setDateState] = useState(date);
    return (
            <div>
            <div className="addNoteComponent">
            <h1 className="addNoteHeading">
                Add New Note
            </h1>
            <div className="noteBox">
               <div className="noteBoxContainer">
                  <input type="text" className="addTitle" placeholder="Title" onFocus={()=>setActivateNote(true)}/>
                  {
                      activateNote?<><textarea className="addNote" placeholder="Take a Note" rows="5" cols="10" />  <AddNoteUtility dateActive={dateActive} setDateActive={setDateActive} dateState={dateState} setDateState={setDateState} /></>:null 
                  }
               </div>
            </div>
           
        </div>
            </div>
    )
}

export default AddNewNote
