import React , {useState} from 'react'
import Calendar from 'react-calendar'
import 'react-calendar/dist/Calendar.css';
const AddNewNote = () => {
    const [activateNote,setActivateNote] = useState(false);
    return (
        <div className="addNoteComponent">
            <h1 className="addNoteHeading">
                Add New Note
            </h1>
            <div className="noteBox">
               <div className="noteBoxContainer">
                  <input type="text" className="addTitle" placeholder="Title" onFocus={()=>setActivateNote(!activateNote?true:false)}/>
                  <textarea className="addNote" placeholder="Take a Note" rows="5" cols="10" /> 
               </div>
            </div>

        </div>
    )
}

export default AddNewNote
