import React , {useState} from 'react'
import AddNoteUtility from './AddNoteUtility';
import DisplayNoteUtility from './DisplayNoteUtility';
import nextId from "react-id-generator";
import moment from "moment"
import "../App.css"

const AddNewNote = () => {
    const defaultNote = {
        id:nextId("Default"),
        Title: "Welcome to Mindify",
        Note : "This is a sample note, please feel free to edit or delete this note and Get started with creating your own notes!",
        FullDate : `Thu Jun 17 2021 22:12:01`,
        noteDate : null,
        Month : null,
        Year : null,
        Delete:false,
    }
    const dateObj=new Date();
    const [activateNote,setActivateNote] = useState(false);
    const [dateActive,setDateActive] = useState(false);
    const [dateState,setDateState] = useState(dateObj);
    const [date,setDate] = useState(dateObj.getDate());
    const [month,setMonth] = useState(dateObj.getMonth()+1);
    const [year,setYear] = useState(dateObj.getFullYear());
    const [title,setTitle] = useState("");
    const [note,setNote] = useState("");
    const [noteAdded,setNoteAdded] = useState([defaultNote]);

    console.log(noteAdded);
    return (
        <div>
            <div className="addNoteComponent">
            <h1 className="addNoteHeading">
                Add New Note
            </h1>
            <div className="noteBox">
               <div className="noteBoxContainer">
                  <input type="text" className="addTitle"  placeholder="Title" value={title} onFocus={()=>setActivateNote(true)} onChange={(e)=>{setTitle(e.target.value)}}/>
                  {
                      activateNote?<><textarea className="addNote" placeholder="Take a Note" value={note}  rows="5" cols="10" onChange={(e)=>setNote(e.target.value)} />  <AddNoteUtility dateActive={dateActive} setDateActive={setDateActive} dateState={dateState} setDateState={setDateState} setDate={setDate} setMonth={setMonth} setYear={setYear} title={title} note={note} setTitle={setTitle} setNote={setNote} setNoteAdded={setNoteAdded} date={date} month={month} year={year} fullDate={dateState}/></>:null 
                  }
               </div>
            </div>
           <div className="displayNote">
               <h1 className="displayHeading">Your Note's</h1>
               {
                  noteAdded.map(({Title,Note,FullDate},index)=>{
                      return(
                        <div className="noteBox">
                           <div className="noteBoxContainer">
                              <input type="text" className="addTitle"  placeholder="Title" value={Title} onFocus={()=>setActivateNote(true)} onChange={(e)=>{setTitle(e.target.value)}}/>
                              {
                               <><textarea className="addNote" placeholder="Take a Note" value={Note}  rows="5" cols="10" onChange={(e)=>setNote(e.target.value)} />  <DisplayNoteUtility dateActive={dateActive} setDateActive={setDateActive} dateState={dateState} setDateState={setDateState} setDate={setDate} setMonth={setMonth} setYear={setYear} title={title} note={note} setTitle={setTitle} setNote={setNote} setNoteAdded={setNoteAdded} date={date} month={month} year={year} fullDate={dateState}/></>
                              }
                           </div>
                           <h1>{FullDate}</h1>
                       </div>
                      )
                  })
               }
           </div>
        </div>
            </div>
    )
}

export default AddNewNote
